import React, { useEffect, useRef, useState } from "react";
import io from "socket.io-client";
import Peer from "simple-peer";
import "../Room.css";

const socket = io.connect("https://192.168.29.23:5000");

function Room() {
  const [peers, setPeers] = useState([]);
  const [userName, setUserName] = useState("");
  const [roomID, setRoomID] = useState("");
  const [participants, setParticipants] = useState([]);
  const [stream, setStream] = useState();
  const [micOn, setMicOn] = useState(true);
  const [camOn, setCamOn] = useState(true);
  const [screenSharing, setScreenSharing] = useState(false);

  const userVideo = useRef();
  const peersRef = useRef([]);
  const screenTrackRef = useRef();

  useEffect(() => {
    const room = prompt("Enter Room Code:");
    const name = prompt("Enter Your Name:");

    setRoomID(room);
    setUserName(name);

    navigator.mediaDevices.getUserMedia({ video: true, audio: true }).then((stream) => {
      setStream(stream);
      if (userVideo.current) {
        userVideo.current.srcObject = stream;
      }

      socket.emit("join-room", { roomID: room, userName: name });

      socket.on("all-users", (users) => {
        const peers = [];
        users.forEach((user) => {
          const peer = createPeer(user.id, socket.id, stream, name);
          peersRef.current.push({
            peerID: user.id,
            peer,
            userName: user.userName,
          });
          peers.push({ peerID: user.id, peer, userName: user.userName });
        });
        setPeers(peers);
      });

      socket.on("user-joined", (payload) => {
        const peer = addPeer(payload.signal, payload.callerID, stream, payload.userName);
        peersRef.current.push({
          peerID: payload.callerID,
          peer,
          userName: payload.userName,
        });

        setPeers((prev) => [...prev, { peerID: payload.callerID, peer, userName: payload.userName }]);
      });

      socket.on("receiving-returned-signal", (payload) => {
        const item = peersRef.current.find((p) => p.peerID === payload.id);
        if (item) {
          try {
            item.peer.signal(payload.signal);
          } catch (err) {
            console.error("Signal error:", err);
          }
        }
      });

      socket.on("user-disconnected", (id) => {
        const peerObj = peersRef.current.find((p) => p.peerID === id);
        if (peerObj) {
          peerObj.peer.destroy();
        }
        peersRef.current = peersRef.current.filter((p) => p.peerID !== id);
        setPeers((prev) => prev.filter((p) => p.peerID !== id));
        setParticipants((prev) => prev.filter((p) => p.id !== id));
      });
    });

    socket.on("update-participants", (list) => {
      setParticipants(list);
    });

    return () => {
      socket.disconnect();
    };
  }, []);

  function createPeer(userToSignal, callerID, stream, userName) {
    const peer = new Peer({
      initiator: true,
      trickle: false,
      stream,
    });

    peer.on("signal", (signal) => {
      socket.emit("sending-signal", {
        userToSignal,
        callerID,
        signal,
        userName,
      });
    });

    return peer;
  }

  function addPeer(incomingSignal, callerID, stream, userName) {
    const peer = new Peer({
      initiator: false,
      trickle: false,
      stream,
    });

    peer.on("signal", (signal) => {
      socket.emit("returning-signal", { signal, callerID });
    });

    try {
      peer.signal(incomingSignal);
    } catch (err) {
      console.error("Incoming signal error:", err);
    }

    return peer;
  }

  function toggleMic() {
    stream.getAudioTracks()[0].enabled = !micOn;
    setMicOn(!micOn);
  }

  function toggleCam() {
    stream.getVideoTracks()[0].enabled = !camOn;
    setCamOn(!camOn);
  }

  function handleShareScreen() {
    if (!screenSharing) {
      navigator.mediaDevices.getDisplayMedia({ cursor: true }).then((screenStream) => {
        const screenTrack = screenStream.getTracks()[0];
        peersRef.current.forEach(({ peer }) => {
          peer.replaceTrack(stream.getVideoTracks()[0], screenTrack, stream);
        });
        screenTrack.onended = () => {
          peersRef.current.forEach(({ peer }) => {
            peer.replaceTrack(screenTrack, stream.getVideoTracks()[0], stream);
          });
          setScreenSharing(false);
        };
        screenTrackRef.current = screenTrack;
        setScreenSharing(true);
      });
    }
  }

  function endCall() {
    peersRef.current.forEach(({ peer }) => peer.destroy());
    socket.disconnect();
    window.location.href = "/";
  }

  return (
    <div className="room-container">
      <div className="header">
        <h1 className="logo">Ve <span>Meet</span></h1>
        <div className="room-info">
          <p><strong>Room:</strong> {roomID}</p>
          <p><strong>Host:</strong> {userName}</p>
        </div>
      </div>

      <div className="video-grid">
        <div className="video-block" key="host">
          <video ref={userVideo} autoPlay muted playsInline className="video" />
          <div className="name-label">{userName} (You)</div>
        </div>
        {peers.map(({ peer, peerID, userName }) => (
          <Video key={peerID} peer={peer} name={userName} />
        ))}
      </div>

      <div className="controls">
        <button onClick={toggleMic}>{micOn ? "Mute" : "Unmute"}</button>
        <button onClick={toggleCam}>{camOn ? "Video Off" : "Video On"}</button>
        <button onClick={handleShareScreen} disabled={screenSharing}>
          Share Screen
        </button>
        <button onClick={endCall} className="end-btn">End Meeting</button>
      </div>

      <div className="participants-panel">
        <h3>Participants ({participants.length + 1})</h3>
        <ul>
          <li key="host-name">{userName} (You)</li>
          {participants.map(({ name, id }) => (
            <li key={id}>{name}</li>
          ))}
        </ul>
      </div>
    </div>
  );
}

const Video = ({ peer, name }) => {
  const ref = useRef();

  useEffect(() => {
    peer.on("stream", (stream) => {
      if (ref.current) {
        ref.current.srcObject = stream;
      }
    });
  }, [peer]);

  return (
    <div className="video-block">
      <video playsInline autoPlay ref={ref} className="video" />
      <div className="name-label">{name}</div>
    </div>
  );
};

export default Room;
