// src/components/GuestRoom.js
import React, { useEffect, useRef, useState } from 'react';
import io from 'socket.io-client';
import Peer from 'simple-peer';
import '../Room.css';

const socket = io('http://localhost:5000'); // Your signaling server

const GuestRoom = () => {
  const [peers, setPeers] = useState([]);
  const [stream, setStream] = useState(null);
  const [roomId, setRoomId] = useState('');
  const [name, setName] = useState('');
  const [joined, setJoined] = useState(false);
  const [participants, setParticipants] = useState([]);

  const userVideo = useRef();
  const peersRef = useRef([]);

  useEffect(() => {
    socket.on('all users', (users) => {
      const newPeers = [];
      users.forEach(({ socketId, userName }) => {
        const peer = createPeer(socketId, socket.id, stream);
        peersRef.current.push({
          peerID: socketId,
          peer,
          userName,
        });
        newPeers.push({ peer, userName });
      });
      setPeers(newPeers);
    });

    socket.on('user joined', ({ signal, callerID, userName }) => {
      const peer = addPeer(signal, callerID, stream);
      peersRef.current.push({
        peerID: callerID,
        peer,
        userName,
      });

      setPeers((users) => [...users, { peer, userName }]);
    });

    socket.on('receiving returned signal', ({ id, signal }) => {
      const item = peersRef.current.find(p => p.peerID === id);
      if (item) {
        item.peer.signal(signal);
      }
    });

    socket.on('update-users', (names) => {
      setParticipants(names);
    });
  }, [stream]);

  function createPeer(userToSignal, callerID, stream) {
    const peer = new Peer({
      initiator: true,
      trickle: false,
      stream,
    });

    peer.on('signal', signal => {
      socket.emit('sending signal', { userToSignal, callerID, signal, userName: name });
    });

    return peer;
  }

  function addPeer(incomingSignal, callerID, stream) {
    const peer = new Peer({
      initiator: false,
      trickle: false,
      stream,
    });

    peer.on('signal', signal => {
      socket.emit('returning signal', { signal, callerID });
    });

    peer.signal(incomingSignal);
    return peer;
  }

  const handleJoin = async () => {
    try {
      const currentStream = await navigator.mediaDevices.getUserMedia({ video: true, audio: true });
      setStream(currentStream);
      if (userVideo.current) {
        userVideo.current.srcObject = currentStream;
      }
      socket.emit('join room', { roomId, userName: name });
      setJoined(true);
    } catch (err) {
      console.error('Error accessing media devices.', err);
    }
  };

  const leaveRoom = () => {
    window.location.reload(); // Simple way to leave
  };

  return (
    <div className="room-container">
      <div className="video-grid">
        <div className="video-block">
          <video
            ref={userVideo}
            autoPlay
            muted
            playsInline
            className="video"
          />
          <div className="video-name">{name || 'You'}</div>
        </div>

        {peers.map((peerObj, index) => (
          <Video key={index} peer={peerObj.peer} name={peerObj.userName} />
        ))}
      </div>

      <div className="side-panel">
        <h3>Participants ({participants.length})</h3>
        <ul>
          {participants.map((p, idx) => (
            <li key={idx}>{p}</li>
          ))}
        </ul>
        <div className="controls">
          {joined ? (
            <button className="end" onClick={leaveRoom}>Leave</button>
          ) : (
            <>
              <input
                placeholder="Your Name"
                value={name}
                onChange={(e) => setName(e.target.value)}
              />
              <input
                placeholder="Room ID"
                value={roomId}
                onChange={(e) => setRoomId(e.target.value)}
              />
              <button onClick={handleJoin}>Join</button>
            </>
          )}
        </div>
      </div>
    </div>
  );
};

const Video = ({ peer, name }) => {
  const ref = useRef();

  useEffect(() => {
    peer.on('stream', stream => {
      if (ref.current) {
        ref.current.srcObject = stream;
      }
    });
  }, [peer]);

  return (
    <div className="video-block">
      <video playsInline autoPlay ref={ref} className="video" />
      <div className="video-name">{name}</div>
    </div>
  );
};

export default GuestRoom;
