// Room.js (Host)
import React, { useEffect, useRef, useState } from "react";
import io from "socket.io-client";
import Peer from "simple-peer";
import "./Room.css";

const socket = io.connect("https://192.168.29.23:5000");

const Room = () => {
  const [peers, setPeers] = useState([]);
  const [participants, setParticipants] = useState([]);
  const [roomId, setRoomId] = useState("");
  const [userName, setUserName] = useState("");
  const userVideo = useRef();
  const peersRef = useRef([]);
  const videoGridRef = useRef();

  useEffect(() => {
    const name = prompt("Enter your name");
    const room = prompt("Enter Room ID");

    setUserName(name);
    setRoomId(room);

    navigator.mediaDevices.getUserMedia({ video: true, audio: true }).then((stream) => {
      if (userVideo.current) {
        userVideo.current.srcObject = stream;
      }

      socket.emit("join-room", { roomId: room, userName: name });

      socket.on("all-users", (users) => {
        const newPeers = [];
        users.forEach(({ socketId, userName }) => {
          const peer = createPeer(socketId, socket.id, stream);
          peersRef.current.push({ peerID: socketId, peer, userName });
          newPeers.push({ peerID: socketId, peer, userName });
        });
        setPeers(newPeers);
      });

      socket.on("user-joined", ({ signal, callerID, userName }) => {
        const peer = addPeer(signal, callerID, stream);
        peersRef.current.push({ peerID: callerID, peer, userName });
        setPeers((prev) => [...prev, { peerID: callerID, peer, userName }]);
      });

      socket.on("receiving-returned-signal", ({ id, signal }) => {
        const item = peersRef.current.find((p) => p.peerID === id);
        if (item) {
          item.peer.signal(signal);
        }
      });

      socket.on("update-participants", (list) => {
        setParticipants(list);
      });

      socket.on("user-disconnected", (id) => {
        const peerObj = peersRef.current.find((p) => p.peerID === id);
        if (peerObj) {
          peerObj.peer.destroy();
        }
        peersRef.current = peersRef.current.filter((p) => p.peerID !== id);
        setPeers((prev) => prev.filter((p) => p.peerID !== id));
      });
    });

    return () => {
      socket.disconnect();
    };
  }, []);

  const createPeer = (userToSignal, callerID, stream) => {
    const peer = new Peer({
      initiator: true,
      trickle: false,
      stream,
    });

    peer.on("signal", (signal) => {
      socket.emit("sending-signal", {
        userToSignal,
        callerID,
        signal,
        userName,
      });
    });

    return peer;
  };

  const addPeer = (incomingSignal, callerID, stream) => {
    const peer = new Peer({
      initiator: false,
      trickle: false,
      stream,
    });

    peer.on("signal", (signal) => {
      socket.emit("returning-signal", { signal, callerID });
    });

    peer.signal(incomingSignal);
    return peer;
  };

  const endMeeting = () => {
    socket.emit("end-meeting", roomId);
    window.location.href = "/";
  };

  return (
    <div className="room-container">
      <header className="room-header">
        <h1 className="logo-text">Ve Meet</h1>
        <div className="room-info">
          <span>Room: {roomId}</span>
          <span>Host: {userName}</span>
        </div>
        <button className="end-button" onClick={endMeeting}>End Meeting</button>
      </header>

      <main className="video-section">
        <div className="video-grid" ref={videoGridRef}>
          <div className="video-block" key="host">
            <video playsInline muted ref={userVideo} autoPlay className="video" />
            <div className="name-tag">{userName} (You)</div>
          </div>
          {peers.map(({ peerID, peer, userName }) => (
            <Video key={peerID} peer={peer} userName={userName} />
          ))}
        </div>

        <div className="participant-list">
          <h3>Participants ({participants.length})</h3>
          <ul>
            {participants.map((name, idx) => (
              <li key={`${name}-${idx}`}>{name}</li>
            ))}
          </ul>
        </div>
      </main>
    </div>
  );
};

const Video = ({ peer, userName }) => {
  const ref = useRef();

  useEffect(() => {
    peer.on("stream", (stream) => {
      if (ref.current) {
        ref.current.srcObject = stream;
      }
    });
  }, [peer]);

  return (
    <div className="video-block">
      <video playsInline autoPlay ref={ref} className="video" />
      <div className="name-tag">{userName}</div>
    </div>
  );
};

export default Room;
