import React, { useEffect, useRef, useState } from "react";
import io from "socket.io-client";
import Peer from "simple-peer";
import "./Room.css";

const socket = io("https://192.168.29.23:5000", { secure: true });

const GuestRoom = () => {
  const [roomId, setRoomId] = useState("");
  const [userName, setUserName] = useState("");
  const [joined, setJoined] = useState(false);
  const [peers, setPeers] = useState({});
  const [stream, setStream] = useState(null);
  const [isMuted, setIsMuted] = useState(false);
  const [videoOff, setVideoOff] = useState(false);
  const [participants, setParticipants] = useState([]);

  const userVideo = useRef();
  const peersRef = useRef({});

  useEffect(() => {
    socket.on("user-joined", ({ id, name }) => {
      const peer = createPeer(id, socket.id, stream);
      peersRef.current[id] = { peer, name };
      setPeers((prev) => ({ ...prev, [id]: { peer, name } }));
    });

    socket.on("offer", async ({ from, offer, name }) => {
      const peer = addPeer(offer, from, stream);
      peersRef.current[from] = { peer, name };
      setPeers((prev) => ({ ...prev, [from]: { peer, name } }));
    });

    socket.on("answer", ({ from, answer }) => {
      const peer = peersRef.current[from]?.peer;
      if (peer) peer.signal(answer);
    });

    socket.on("ice-candidate", ({ from, candidate }) => {
      const peer = peersRef.current[from]?.peer;
      if (peer) peer.signal(candidate);
    });

    socket.on("user-left", (id) => {
      if (peersRef.current[id]) {
        peersRef.current[id].peer.destroy();
        delete peersRef.current[id];
        setPeers((prev) => {
          const updated = { ...prev };
          delete updated[id];
          return updated;
        });
      }
    });

    socket.on("user-list", (list) => {
      setParticipants(list);
    });

    return () => {
      socket.disconnect();
    };
  }, [stream]);

  const handleJoin = async () => {
    try {
      const media = await navigator.mediaDevices.getUserMedia({ video: true, audio: true });
      setStream(media);
      if (userVideo.current) {
        userVideo.current.srcObject = media;
      }

      socket.emit("join-room", { roomId, userName });
      setJoined(true);
    } catch (err) {
      alert("Error accessing media devices.");
      console.error(err);
    }
  };

  const createPeer = (userToSignal, callerID, stream) => {
    const peer = new Peer({
      initiator: true,
      trickle: false,
      stream
    });

    peer.on("signal", (signal) => {
      socket.emit("offer", {
        to: userToSignal,
        offer: signal
      });
    });

    peer.on("error", err => console.error("Peer error", err));

    return peer;
  };

  const addPeer = (incomingSignal, callerID, stream) => {
    const peer = new Peer({
      initiator: false,
      trickle: false,
      stream
    });

    peer.on("signal", (signal) => {
      socket.emit("answer", {
        to: callerID,
        answer: signal
      });
    });

    peer.on("error", err => console.error("Peer error", err));

    peer.signal(incomingSignal);

    return peer;
  };

  const toggleMute = () => {
    stream.getAudioTracks()[0].enabled = isMuted;
    setIsMuted(!isMuted);
  };

  const toggleVideo = () => {
    stream.getVideoTracks()[0].enabled = videoOff;
    setVideoOff(!videoOff);
  };

  const leaveRoom = () => {
    window.location.href = "/";
  };

  return (
    <div className="room-container">
      {!joined ? (
        <div className="join-container">
          <h1 className="logo">Ve <span>Meet</span></h1>
          <input type="text" placeholder="Enter Room Code" value={roomId} onChange={(e) => setRoomId(e.target.value)} />
          <input type="text" placeholder="Enter Your Name" value={userName} onChange={(e) => setUserName(e.target.value)} />
          <button className="join-btn" onClick={handleJoin}>Join</button>
        </div>
      ) : (
        <>
          <div className="video-grid">
            <div className="video-block">
              <video ref={userVideo} autoPlay muted playsInline className="video" />
              <div className="name-label">{userName} (You)</div>
            </div>
            {Object.entries(peers).map(([id, { peer, name }]) => (
              <Video key={id} peer={peer} name={name} />
            ))}
          </div>
          <div className="controls">
            <button onClick={toggleMute}>{isMuted ? "Unmute" : "Mute"}</button>
            <button onClick={toggleVideo}>{videoOff ? "Start Video" : "Stop Video"}</button>
            <button onClick={leaveRoom}>Leave</button>
          </div>
        </>
      )}
    </div>
  );
};

const Video = ({ peer, name }) => {
  const ref = useRef();

  useEffect(() => {
    peer.on("stream", stream => {
      if (ref.current) {
        ref.current.srcObject = stream;
      }
    });
  }, [peer]);

  return (
    <div className="video-block">
      <video ref={ref} autoPlay playsInline className="video" />
      <div className="name-label">{name}</div>
    </div>
  );
};

export default GuestRoom;
