import React, { useEffect, useRef, useState } from "react";
import io from "socket.io-client";
import Peer from "simple-peer";
import "./Room.css";

const Room = () => {
  const [peers, setPeers] = useState([]);
  const [participants, setParticipants] = useState([]);
  const [stream, setStream] = useState();
  const [screenStream, setScreenStream] = useState(null);
  const [name, setName] = useState("");
  const [roomID, setRoomID] = useState("");
  const [joined, setJoined] = useState(false);
  const userVideo = useRef();
  const peersRef = useRef([]);
  const socketRef = useRef();
  const screenSharingRef = useRef(false);

  useEffect(() => {
    const urlParams = new URLSearchParams(window.location.search);
    const nameParam = urlParams.get("name");
    const roomParam = urlParams.get("room");

    if (nameParam && roomParam) {
      setName(nameParam);
      setRoomID(roomParam);
    }
  }, []);

  const handleJoin = () => {
    socketRef.current = io.connect("https://192.168.29.23:5000");

    navigator.mediaDevices.getUserMedia({ video: true, audio: true }).then((stream) => {
      setStream(stream);
      if (userVideo.current) {
        userVideo.current.srcObject = stream;
      }

      socketRef.current.emit("join-room", { roomID, name });

      socketRef.current.on("all-users", (users) => {
        const peers = [];
        users.forEach(({ socketID, userName }) => {
          const peer = createPeer(socketID, socketRef.current.id, stream);
          peersRef.current.push({ peerID: socketID, peer, userName });
          peers.push({ peerID: socketID, peer, userName });
        });
        setPeers(peers);
      });

      socketRef.current.on("user-joined", ({ signal, callerID, userName }) => {
        const peer = addPeer(signal, callerID, stream);
        peersRef.current.push({ peerID: callerID, peer, userName });
        setPeers((users) => [...users, { peerID: callerID, peer, userName }]);
      });

      socketRef.current.on("receiving-returned-signal", ({ id, signal }) => {
        const item = peersRef.current.find(p => p.peerID === id);
        if (item) item.peer.signal(signal);
      });

      socketRef.current.on("user-left", (id) => {
        const peerObj = peersRef.current.find(p => p.peerID === id);
        if (peerObj) {
          peerObj.peer.destroy();
        }
        peersRef.current = peersRef.current.filter(p => p.peerID !== id);
        setPeers(peers => peers.filter(p => p.peerID !== id));
      });

      socketRef.current.on("update-participants", (list) => {
        setParticipants(list);
      });
    });

    setJoined(true);
  };

  const createPeer = (userToSignal, callerID, stream) => {
    const peer = new Peer({
      initiator: true,
      trickle: false,
      stream,
    });

    peer.on("signal", signal => {
      socketRef.current.emit("sending-signal", {
        userToSignal,
        callerID,
        signal,
        userName: name,
      });
    });

    return peer;
  };

  const addPeer = (incomingSignal, callerID, stream) => {
    const peer = new Peer({
      initiator: false,
      trickle: false,
      stream,
    });

    peer.on("signal", signal => {
      socketRef.current.emit("returning-signal", { signal, callerID });
    });

    peer.signal(incomingSignal);
    return peer;
  };

  const startScreenShare = async () => {
    if (screenSharingRef.current) return;

    try {
      const screen = await navigator.mediaDevices.getDisplayMedia({ video: true });
      setScreenStream(screen);
      screenSharingRef.current = true;

      // Send screen as new peer to everyone
      peersRef.current.forEach(({ peerID }) => {
        const screenPeer = new Peer({
          initiator: true,
          trickle: false,
          stream: screen,
        });

        screenPeer.on("signal", (signal) => {
          socketRef.current.emit("sending-signal", {
            userToSignal: peerID,
            callerID: socketRef.current.id + "-screen",
            signal,
            userName: "üì∫ Shared Screen",
            isScreen: true,
          });
        });
      });

      screen.getTracks()[0].onended = () => {
        stopScreenShare();
      };
    } catch (err) {
      console.error("Error sharing screen", err);
    }
  };

  const stopScreenShare = () => {
    if (!screenStream) return;
    screenStream.getTracks().forEach(track => track.stop());
    setScreenStream(null);
    screenSharingRef.current = false;
  };

  const toggleMic = () => {
    stream.getAudioTracks()[0].enabled = !stream.getAudioTracks()[0].enabled;
  };

  const toggleCamera = () => {
    stream.getVideoTracks()[0].enabled = !stream.getVideoTracks()[0].enabled;
  };

  const endMeeting = () => {
    peersRef.current.forEach(p => p.peer.destroy());
    if (stream) {
      stream.getTracks().forEach(track => track.stop());
    }
    if (screenStream) {
      screenStream.getTracks().forEach(track => track.stop());
    }
    socketRef.current.disconnect();
    window.location.href = "/";
  };

  return (
    <div className="room-container">
      <header className="room-header">
        <div className="logo-text">Ve <span>Meet</span></div>
        <div className="room-info">
          <div className="room-code">Room: {roomID}</div>
          <div className="your-name">You: {name}</div>
        </div>
      </header>

      {!joined ? (
        <div className="join-screen">
          <button className="join-btn" onClick={handleJoin}>Join Room</button>
        </div>
      ) : (
        <div className="room-content">
          <div className="video-grid">
            <div className="video-block">
              <video ref={userVideo} autoPlay muted playsInline />
              <div className="video-name">{name} (You)</div>
            </div>
            {peers.map((peer) => (
              <Video key={peer.peerID} peer={peer.peer} name={peer.userName} />
            ))}
          </div>

          <div className="controls">
            <button onClick={toggleMic}>üé§</button>
            <button onClick={toggleCamera}>üì∑</button>
            <button onClick={startScreenShare}>üñ•Ô∏è Share Screen</button>
            <button onClick={endMeeting}>‚ùå End</button>
          </div>

          <div className="participants-panel">
            <h4>Participants ({participants.length})</h4>
            <ul>
              {participants.map((p, i) => <li key={i}>{p}</li>)}
            </ul>
          </div>
        </div>
      )}
    </div>
  );
};

const Video = ({ peer, name }) => {
  const ref = useRef();

  useEffect(() => {
    peer.on("stream", stream => {
      if (ref.current) {
        ref.current.srcObject = stream;
      }
    });
  }, [peer]);

  return (
    <div className="video-block">
      <video playsInline autoPlay ref={ref} />
      <div className="video-name">{name}</div>
    </div>
  );
};

export default Room;
