// GuestRoom.js

import React, { useEffect, useRef, useState } from "react";
import io from "socket.io-client";
import Peer from "simple-peer";
import "./GuestRoom.css";

const socket = io.connect("https://192.168.29.23:5000");

const GuestRoom = () => {
  const [peers, setPeers] = useState([]);
  const [screenShareStream, setScreenShareStream] = useState(null);
  const [name, setName] = useState("");
  const [roomID, setRoomID] = useState("");
  const [joined, setJoined] = useState(false);
  const [localStream, setLocalStream] = useState(null);
  const userVideo = useRef();
  const peersRef = useRef([]);

  useEffect(() => {
    const storedRoomID = localStorage.getItem("roomID");
    if (storedRoomID) setRoomID(storedRoomID);
  }, []);

  useEffect(() => {
    if (userVideo.current && localStream) {
      userVideo.current.srcObject = localStream;
    }
  }, [localStream]);

  const handleJoin = async () => {
    if (!name.trim()) return alert("Please enter your name.");

    try {
      const stream = await navigator.mediaDevices.getUserMedia({
        video: true,
        audio: true,
      });
      setLocalStream(stream);
      setJoined(true);
      socket.emit("join-room", { roomID, name });

      socket.on("all-users", (users) => {
        const peers = [];
        users.forEach(({ socketID, userName }) => {
          const peer = createPeer(socketID, socket.id, stream);
          peersRef.current.push({ peerID: socketID, peer, userName });
          peers.push({ peerID: socketID, peer, userName });
        });
        setPeers(peers);
      });

      socket.on("user-joined", ({ signal, callerID, userName }) => {
        const peer = addPeer(signal, callerID, stream);
        peersRef.current.push({ peerID: callerID, peer, userName });
        setPeers((users) => [...users, { peerID: callerID, peer, userName }]);
      });

      socket.on("receiving-returned-signal", ({ id, signal }) => {
        const item = peersRef.current.find((p) => p.peerID === id);
        item?.peer.signal(signal);
      });

      socket.on("user-disconnected", (id) => {
        const peerObj = peersRef.current.find((p) => p.peerID === id);
        if (peerObj) {
          peerObj.peer.destroy();
        }
        peersRef.current = peersRef.current.filter((p) => p.peerID !== id);
        setPeers((users) => users.filter((p) => p.peerID !== id));
      });

      socket.on("screen-share-started", ({ streamID }) => {
        const screenTrack = new MediaStream();
        screenTrack.id = streamID;
        socket.emit("request-screen-stream", streamID);
      });

      socket.on("receive-screen-stream", (stream) => {
        setScreenShareStream(stream);
      });

    } catch (err) {
      console.error("Error accessing media devices.", err);
    }
  };

  const createPeer = (userToSignal, callerID, stream) => {
    const peer = new Peer({
      initiator: true,
      trickle: false,
      stream,
    });

    peer.on("signal", (signal) => {
      socket.emit("sending-signal", {
        userToSignal,
        callerID,
        signal,
        userName: name,
      });
    });

    return peer;
  };

  const addPeer = (incomingSignal, callerID, stream) => {
    const peer = new Peer({
      initiator: false,
      trickle: false,
      stream,
    });

    peer.on("signal", (signal) => {
      socket.emit("returning-signal", { signal, callerID });
    });

    peer.signal(incomingSignal);
    return peer;
  };

  const toggleFullscreen = () => {
    const video = document.getElementById("screen-share-video");
    if (video.requestFullscreen) video.requestFullscreen();
    else if (video.webkitRequestFullscreen) video.webkitRequestFullscreen();
    else if (video.msRequestFullscreen) video.msRequestFullscreen();
  };

  return (
    <div className="guest-room-container">
      {!joined ? (
        <div className="join-ui">
          <h2 className="ve-meet-logo">Ve Meet</h2>
          <input
            type="text"
            placeholder="Enter your name"
            value={name}
            onChange={(e) => setName(e.target.value)}
          />
          <button onClick={handleJoin}>Join</button>
        </div>
      ) : (
        <div className="video-grid-container">
          <div className="video-grid">
            <div className="video-block">
              <video
                muted
                ref={userVideo}
                autoPlay
                playsInline
                className="video-element"
              />
              <div className="name-label">{name} (You)</div>
            </div>

            {peers.map(({ peerID, peer, userName }) => (
              <Video key={peerID} peer={peer} name={userName} />
            ))}
          </div>

          {screenShareStream && (
            <div className="screen-share-block">
              <video
                id="screen-share-video"
                className="screen-share-video"
                autoPlay
                playsInline
                ref={(video) => {
                  if (video) video.srcObject = screenShareStream;
                }}
              />
              <button className="fullscreen-btn" onClick={toggleFullscreen}>
                â›¶
              </button>
            </div>
          )}
        </div>
      )}
    </div>
  );
};

const Video = ({ peer, name }) => {
  const ref = useRef();

  useEffect(() => {
    peer.on("stream", (stream) => {
      if (ref.current) ref.current.srcObject = stream;
    });
  }, [peer]);

  return (
    <div className="video-block">
      <video playsInline autoPlay ref={ref} className="video-element" />
      <div className="name-label">{name}</div>
    </div>
  );
};

export default GuestRoom;
