// src/components/GuestRoom.js
import React, { useEffect, useRef, useState } from "react";
import { useLocation, useParams, useNavigate } from "react-router-dom";
import io from "socket.io-client";
import Peer from "simple-peer";
import "./Room.css";

const socket = io("https://192.168.29.23:5000", { secure: true });

const GuestRoom = () => {
  const { roomId } = useParams();
  const location = useLocation();
  const navigate = useNavigate();
  const [name] = useState(location.state?.name || "Guest");
  const [peers, setPeers] = useState([]);
  const [stream, setStream] = useState(null);
  const [videoEnabled, setVideoEnabled] = useState(true);
  const [audioEnabled, setAudioEnabled] = useState(true);
  const userVideo = useRef();
  const peersRef = useRef([]);

  useEffect(() => {
    navigator.mediaDevices.getUserMedia({ video: true, audio: true }).then((currentStream) => {
      setStream(currentStream);
      if (userVideo.current) {
        userVideo.current.srcObject = currentStream;
      }

      socket.emit("join-room", { roomId, name });

      socket.on("all-users", (users) => {
        const peersList = [];
        users.forEach((user) => {
          const peer = createPeer(user.socketId, socket.id, currentStream);
          peersRef.current.push({
            peerID: user.socketId,
            peer,
            name: user.name,
          });
          peersList.push({ peerID: user.socketId, peer, name: user.name });
        });
        setPeers(peersList);
      });

      socket.on("user-joined", (payload) => {
        const peer = addPeer(payload.signal, payload.callerId, currentStream);
        peersRef.current.push({
          peerID: payload.callerId,
          peer,
          name: payload.name,
        });

        setPeers((users) => [...users, { peerID: payload.callerId, peer, name: payload.name }]);
      });

      socket.on("receiving-returned-signal", (payload) => {
        const item = peersRef.current.find((p) => p.peerID === payload.id);
        item?.peer.signal(payload.signal);
      });

      socket.on("user-disconnected", (socketId) => {
        const peerObj = peersRef.current.find((p) => p.peerID === socketId);
        if (peerObj) {
          peerObj.peer.destroy();
        }
        peersRef.current = peersRef.current.filter((p) => p.peerID !== socketId);
        setPeers((users) => users.filter((p) => p.peerID !== socketId));
      });
    });

    return () => {
      socket.disconnect();
      stream?.getTracks().forEach((track) => track.stop());
    };
  }, []);

  const createPeer = (userToSignal, callerId, stream) => {
    const peer = new Peer({
      initiator: true,
      trickle: false,
      stream,
    });

    peer.on("signal", (signal) => {
      socket.emit("sending-signal", {
        userToSignal,
        callerId,
        signal,
        name,
      });
    });

    return peer;
  };

  const addPeer = (incomingSignal, callerId, stream) => {
    const peer = new Peer({
      initiator: false,
      trickle: false,
      stream,
    });

    peer.on("signal", (signal) => {
      socket.emit("returning-signal", { signal, callerId });
    });

    peer.signal(incomingSignal);
    return peer;
  };

  const toggleVideo = () => {
    stream.getVideoTracks()[0].enabled = !videoEnabled;
    setVideoEnabled((prev) => !prev);
  };

  const toggleAudio = () => {
    stream.getAudioTracks()[0].enabled = !audioEnabled;
    setAudioEnabled((prev) => !prev);
  };

  const leaveMeeting = () => {
    stream.getTracks().forEach((track) => track.stop());
    socket.disconnect();
    navigate("/guest");
  };

  return (
    <div className="roomContainer">
      <h1 className="logoText">Ve Meet</h1>
      <div className="videoGrid">
        <div className="videoContainer">
          <video ref={userVideo} autoPlay muted className="videoElement" />
          <p className="nameTag">{name} (You)</p>
        </div>
        {peers.map((peer) => (
          <Video key={peer.peerID} peer={peer.peer} name={peer.name} />
        ))}
      </div>

      <div className="controlBar">
        <button className="controlBtn" onClick={toggleAudio}>
          {audioEnabled ? "Mute" : "Unmute"}
        </button>
        <button className="controlBtn" onClick={toggleVideo}>
          {videoEnabled ? "Video Off" : "Video On"}
        </button>
        <button className="endBtn" onClick={leaveMeeting}>Leave</button>
      </div>
    </div>
  );
};

const Video = ({ peer, name }) => {
  const ref = useRef();

  useEffect(() => {
    peer.on("stream", (stream) => {
      if (ref.current) {
        ref.current.srcObject = stream;
      }
    });
  }, [peer]);

  return (
    <div className="videoContainer">
      <video ref={ref} autoPlay playsInline className="videoElement" />
      <p className="nameTag">{name}</p>
    </div>
  );
};

export default GuestRoom;
