// src/components/GuestHome.js
import React, { useState } from "react";
import { useNavigate } from "react-router-dom";
import "./Room.css"; // reuse same styling as host for golden-black theme

const GuestHome = () => {
  const [roomId, setRoomId] = useState("");
  const [name, setName] = useState("");
  const navigate = useNavigate();

  const handleJoin = (e) => {
    e.preventDefault();
    if (roomId && name) {
      navigate(`/guest/${roomId}`, { state: { name } });
    }
  };

  return (
    <div className="roomContainer">
      <h1 className="logoText">Ve Meet</h1>
      <form onSubmit={handleJoin} className="formContainer">
        <input
          className="inputBox"
          type="text"
          placeholder="Enter Your Name"
          value={name}
          onChange={(e) => setName(e.target.value)}
          required
        />
        <input
          className="inputBox"
          type="text"
          placeholder="Enter Room Code"
          value={roomId}
          onChange={(e) => setRoomId(e.target.value)}
          required
        />
        <button className="actionBtn" type="submit">Join Room</button>
      </form>
    </div>
  );
};

export default GuestHome;
